//VARS

//Fonts
@font-face {
  font-family: 'Liberator';
  src: url('./../core/content/fonts/LiberatorSIROKO-Medium.ttf');
}
@font-face {
  font-family: 'Inter';
  src: url('./../core/content/fonts/Inter-VariableFont_slnt,wght.ttf');
}
@font-face {
  font-family: 'Inconsolata';
  src: url('./../core/content/fonts/Inconsolata-VAriableFont_wdth,wght.ttf');
}

$font-main: 'Inter', sans-serif;
$font-box: 'Inconsolata', sans-serif;

//Color
$color-primary: #311429;
$color-secondary: #339b95;
$color-white: #ffffff;
$color-black: #000000;

// Media-querys
$tablet: 768px;
$web: 1024px;

body {
  position: relative;
  margin: 0;
  box-sizing: border-box;
  padding: 50px 75px 0px;
  background-image: url('./../core/content/img/background-siroko.jpg');
  background-size: cover;
  background-repeat: no-repeat;


  @media all and (min-width: $web) {
    padding: 300px;

  }

  @media all and (max-width: $web) {
    height: 100vh;
  }

  p {
    margin: 0;
    padding: 0;
  }
}

.header {
  display: grid;
  grid-gap: 1rem;
  align-content: center;

  &__logoSmall {
    margin-bottom: 1.75rem;
    height: 70px;
  }

  &__logoExtended {
    display: none;
  }

  @media all and (min-width: $web) {
    &__logoSmall {
      display: none;
    }

    &__logoExtended {
      display: block;
      margin-bottom: 28px;
      height: 70px;
    }
  }

  &__steps {
    display: flex;
    text-transform: uppercase;
    padding: 3px 7px;
    border-radius: 3px;
    width: fit-content;
    background-color: $color-secondary;
    font-size: 1.125rem;
    font-family: $font-main;
    font-weight: 700;
    color: #2c5e5b;
  }

  &__title {
    text-transform: uppercase;
    margin-bottom: 0;
    font-family: 'Liberator', sans-serif;
    font-size: 4rem;
    font-weight: 800;
    color: $color-white;
  }

  &__introduction {
    font-family: $font-box;
    font-size: 2rem;
    font-weight: 400;
    color: $color-white;
  }
}

.main {
  &__container {
    display: grid;
    grid-gap: 1rem;
    margin-top: 20px;
    border-radius: 3px;
    padding: 2.5rem;
    background-color: $color-primary;

    &--title {
      margin: 0;
      font-size: 1.5rem;
      font-weight: 700;
      font-family: $font-main;
      color: #efe5c3;
    }

    &--code {
      display: flex;
      justify-content: space-between;
      align-items: center;
      gap: 10px;
      border-radius: 3px;
      padding: 10px 16px;
      height: 3.25rem;
      background: linear-gradient(270deg, $color-white 0%, #bdbdbd 100%);

      &__generated {
        font-size: 50px;
        font-family: $font-box;
        font-weight: 700;
        color: $color-black;
      }

      &__copy {
        padding: 0.75rem;
        background: $color-white;
        cursor: pointer;
        font-size: 1.75rem;
        font-family: $font-box;
        font-weight: 700;
        color: $color-black;
      }
    }

    &--description {
      margin: 0;
      font-size: 2rem;
      font-family: $font-box;
      font-weight: 400;
      color: $color-white;
    }

    &--counter {
      display: flex;
      align-items: center;
      padding: 0.625rem 1rem;
      height: 3.25rem;
      background: #2c0c23;

      &__icon {
        width: 3.25rem;
        margin-left: 0.625rem;
      }

      path {
        fill: $color-white;
      }

      &__time {
        margin-left: 0.625rem;
        font-size: 3.25rem;
        font-family: $font-box;
        font-weight: 700;
        color: $color-white;
      }
    }

    &--radiocheck {
      display: flex;
      justify-content: flex-start;
      align-items: center;
      gap: 0.625rem;
      border-radius: 0.25rem;
      padding: 1.875rem;
      font-size: 2.255rem;
      background: rgba(255, 255, 255, 0.09);

      input[type='radio'] {
        place-content: center;
        width: 2.5em;
        height: 2.5em;
      }

      label {
        font-size: 1.875rem;
        font-family: $font-box;
        font-weight: 400;
        color: $color-white;
      }
    }

    &--button {
      display: flex;
      justify-content: center;
      align-items: center;
      text-decoration: none;
      border-radius: 0.25rem;
      height: 1.125rem;
      padding: 2.25rem;
      background-color: $color-secondary;
      color: $color-white;

      &__text {
        display: inline-block;
        text-align: center;
        font-size: 1.875rem;
        font-family: $font-main;
        font-weight: 700;
      }

      &__arrow {
        width: 45px;
        color: #ffffff;

        path {
          fill: $color-white;
        }
      }
    }

    .hide {
      display: none;
    }

    .show {
      display: flex;
      align-items: center;
      background-color: red;

      path {
        fill: $color-white;
      }
    }

    .expired {
      margin-left: 10px;
      padding: 20px 16px;
      font-size: 2rem;
      font-family: $font-box;
      font-weight: 700;
      color: $color-white;

      a {
        color: $color-white;
      }
    }
  }
}

.footer {
  position: absolute;
  bottom: 0;
  margin: 0 -103px;
  min-width: 87.5%;
  padding: 40px 74px;
  font-size: 1.75rem;
  font-family: 'Inconsolata';
  background-color: $color-black;
  color: $color-white;

  @media all and (min-width: $web) {
    margin: 0 -309px;
    width: 100%;
    padding: 40px 300px;
  }
}
